function LoadDribbbleShots(e){var a=[];$.each(e.shots,function(e,t){12>e&&(a.push('<li><a href="'+t.url+'" target="_blank">'),a.push('<img src="'+t.image_teaser_url+'" '),a.push('alt="'+t.title+'"></a></li>'))}),$("#shotsByRafaele").html(a.join(""))}$(document).foundation(),$(document).ready(function(){$.jribbble.getShotsByPlayerId("rafaele",LoadDribbbleShots),$(".banner-slider").unslider({speed:500,delay:5e3,keys:!0,dots:!0})});